import React, { useEffect, useState } from 'react';
import { ungvienAPI } from "../services/api";
import UngVienForm from "../components/UngVienForm";
import UngVienEditModal from "../components/UngVienEditModal";
import "./UngVienPage.css";

const UngVienPage = () => {
  const [ungviens, setUngviens] = useState([]);
  const [editing, setEditing] = useState(null);

  const loadData = async () => {
    const res = await ungvienAPI.getAllUngVien();
    setUngviens(res.data);
  };

  useEffect(() => {
    loadData();
  }, []);

  const handleDelete = async (maUV) => {
    if (window.confirm("Bạn có chắc muốn xóa ứng viên?")) {
      await ungvienAPI.deleteUngVien(maUV);
      loadData();
    }
  };

  const handleVote = async (maUV) => {
    await ungvienAPI.voteUngVien(maUV, 1);
    loadData();
  };

  return (
    <div className="container">
      <h2 className="title">Quản Lý Ứng Viên Bầu Cử</h2>

      <UngVienForm reload={loadData} />

      <h3 className="subtitle">Danh Sách Ứng Viên</h3>
      <table className="table">
        <thead>
          <tr>
            <th>Mã</th>
            <th>Họ Tên</th>
            <th>Địa Chỉ</th>
            <th>Ngày Sinh</th>
            <th>Giới Tính</th>
            <th>Vote</th>
            <th>Hành Động</th>
          </tr>
        </thead>
        <tbody>
          {ungviens.map((uv) => (
            <tr key={uv.Key}>
              <td>{uv.Record.maUV}</td>
              <td>{uv.Record.hoTen}</td>
              <td>{uv.Record.diaChi}</td>
              <td>{uv.Record.ngaySinh}</td>
              <td>{uv.Record.gioiTinh}</td>
              <td className="vote-count">{uv.Record.vote}</td>
              <td className="actions">
                <button className="btn btn-edit" onClick={() => setEditing(uv.Record)}>Sửa</button>
                <button className="btn btn-delete" onClick={() => handleDelete(uv.Record.maUV)}>Xóa</button>
                <button className="btn btn-vote" onClick={() => handleVote(uv.Record.maUV)}>Vote +1</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {editing && (
        <UngVienEditModal
          data={editing}
          close={() => setEditing(null)}
          reload={loadData}
        />
      )}
    </div>
  );
};

export default UngVienPage;
